sys_variable_value.value:
(function execute(inputs, outputs) {
    var data = new Array();
    var document;
    var documents = new Object();

    if (inputs.statusCode == '200') {
        try {
            var response_json = JSON.parse(inputs.responseBody);
            for (var i = 0; i < response_json.length; i++) {
                var document = new Object();
                document.label = response_json[i].displayName;
                document.name = response_json[i].id;
                data.push(document);
            }
        } catch (ex) {
            var dummyDoc = new Object();
            dummyDoc.label = '<No Policies retrieved>';
            dummyDoc.name = null;
            data.push(dummyDoc);
        } finally {
            documents.data = data;
            outputs.documents = documents;
            outputs.status = "Success";
        }
    } 
  else {
        var errMsgs = new OracleBlockStorageErrorHandler().errorHandler(inputs.responseBody);
        outputs.error_message = errMsgs[0];
        outputs.status = errMsgs[1];
    }
})(inputs, outputs);

