sys_variable_value.value:
(function execute(inputs, outputs) {
    if (inputs.statusCode == "200") {
        try {
            var responseData = JSON.parse(inputs.responseBody);
            outputs.status = "Success";
            outputs.id = responseData.id;
            outputs.compartmentid = responseData.compartmentId;
            outputs.displayname = responseData.displayName;
            outputs.timecreated = responseData.timeCreated;
            outputs.destinationregion = responseData.destinationRegion;
            outputs.definedtags = responseData.definedTags;
            outputs.freeformtags = responseData.freeformTags;
            outputs.schedules = responseData.schedules;
        } catch (ex) {
            throw new Error('JSON Parse Error. Response Body may be empty.');
        }
    } else {
        var errMsgs = new OracleBlockStorageErrorHandler().errorHandler(inputs.responseBody);
        outputs.error_message = errMsgs[0];
        outputs.status = errMsgs[1];
    }
})(inputs, outputs);

