sys_variable_value.value:
(function execute(inputs, outputs) {
    var obs = new OracleBlockStorageUtils();
    obs.validateInputVariables(inputs, outputs);
    obs.validateMandatoryVariables(inputs, ["tenancyid"]);
    var requestBody = {};
    if (inputs.tenancyid && inputs.compartmentid) {
        requestBody.compartmentId = inputs.compartmentid;
    } else {
        requestBody.compartmentId = inputs.tenancyid;
    }
    var schedules = inputs.schedules
    if (inputs.displayname) {
        requestBody.displayName = inputs.displayname;
    }
    if (inputs.destinationregion) {
        requestBody.destinationRegion = inputs.destinationregion;
    }
    obs.trimValues(inputs.freeformtags);
    var tags = obs.createTagsPayload(inputs.freeformtags);
    if (Object.keys(tags).length) {
        requestBody.freeformTags = tags;
    }
    var schedulePayload = new Array;

    for (var i = 0; i < schedules.length; i++) {
        var obj = obs.ValidatePayload(schedules[i]);
        schedulePayload.push(obj);
    }

    if (schedules.length) {
        requestBody.schedules = schedulePayload;
    }
    outputs.payload = JSON.stringify(requestBody);
})(inputs, outputs);

